import moment from 'moment';

class Order {
  constructor(id, items, date, points) {
    this.id = id;
    this.items = items;
    this.points = 0;
    this.date = date;
  }

  get readableDate() {
    return moment(this.date).format('MMMM Do YYYY, hh:mm');
  }
}

export default Order;
