import React, { useState } from 'react';
import { View, Text, Button, StyleSheet } from 'react-native';

import Colors from '../../constants/Colors';
import Card from '../UI/Card';

const CommitmentItem = props => {
  const [showDetails, setShowDetails] = useState(false);

  return (
    <Card style={styles.commitmentItem}>
      <View style={styles.summary}>
        {/* <Text style={styles.totalAmount}>${props.amount.toFixed(2)}</Text> */}
        <Text style={styles.date}>{props.date}</Text>
        <Text style={styles.date}>{props.items.title}</Text>
      </View>
      <Button
        color={Colors.primary}
        title={showDetails ? 'הסתר פרטים' : 'ראה פרטים'}
        onPress={() => {
          setShowDetails(prevState => !prevState);
        }}
      />
      {showDetails && (
        <View style={styles.detailItems}>
          <Text>נושא ההתנדבות: {props.items.title}</Text>
          <Text>מיקום: {props.items.location}</Text>
          <Text>תאריך ושעת הגעה: {props.items.time}</Text>
          <Text>מספר הנקודות שהרווחתי: {props.points}</Text>
          <Text>תאריך התחלת התנדבות: {props.items.date}</Text>
        </View>
      )}
    </Card>
  );
};

const styles = StyleSheet.create({
  commitmentItem: {
    margin: 20,
    padding: 10,
    alignItems: 'center'
  },
  summary: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    width: '100%',
    marginBottom: 15
  },
  totalAmount: {
    fontWeight: 'bold',
    fontSize: 16
  },
  date: {
    fontSize: 16,
    color: '#888'
  },
  detailItems: {
    width: '100%',
    alignItems: 'center',

  }
});

export default CommitmentItem;
